.data
FEM_CAT_ADDRESS: 	.word 0
DEAD_FISH_ADDRESS: 	.word 0
LEVEL: 			.word 1

.eqv 	BASE_ADDRESS 		0x10008000 
.eqv	SHIFT_NEXT_ROW		256

#####################################################################
# Player Input Keys
.eqv	KEY_ADDR	0xFFFF0000	# keystroke event address location
.eqv	KEY_W		0x00000077	# hex value for ascii code corresponding to 'w'
.eqv	KEY_A		0x00000061	# hex value for ascii code corresponding to 'a'
.eqv	KEY_S		0x00000073	# hex value for ascii code corresponding to 's'
.eqv	KEY_D		0x00000064	# hex value for ascii code corresponding to 'd'
.eqv	KEY_P		0x00000070	# hex value for ascii code corresponding to 'p'
.eqv 	KEY_SPACE	0x00000020	# hex value for ascii code corresponding to ' '
#####################################################################
# Basic Colours
.eqv	BG_COLOUR	0x1f1f1f
.eqv	WHITE		0xFFFFFF
.eqv	RED		0xFF0000
.eqv 	BLACK		0x000000
.eqv	THUNDER		0xfeef00
.eqv	RED_HEART	0xff1313
.eqv	HEART_BORDER	0x7d3e2d

# Additional Colours for Cats
.eqv 	ORANGE		0xffac63
.eqv 	DARKGREY	0x000000
.eqv 	PINK_BLUSH	0xffadc6
.eqv 	LILAC		0xd1a3ff
.eqv 	GREY		0xa3a3a3
.eqv	BLUE_HAIR	0x6786ff

# Colours for Platform
.eqv 	TOP_PLATFORM_CLR	0x59ff45
.eqv 	BOTTOM_PLATFORM_CLR	0xa67c52

# Colours for Sea Platform
.eqv 	HIGHLIGHT_SEA_CLR	0xe6f5f4
.eqv 	LIGHT_SEA_CLR		0x22cfe0
.eqv 	DARK_SEA_CLR		0x3c7fae

# Colours for Fire
.eqv	ORANGE_FIRE	0xfa6707
.eqv 	YELLOW		0xf5d400

# Colours for Fish
.eqv	MAIN_BLUE	0x44ffe7
.eqv 	PALE_BLUE	0xa1f9d2
.eqv	MAIN_DEAD_FISH	0xa29434
.eqv	PALE_DEAD_FISH	0xd9d5b3
.eqv	EYE_DEAD_FISH	0x3a1515

# Colours for Trophy
.eqv	TROPHY_CLR_1	0xffee1e
.eqv	TROPHY_CLR_2	0xfff8a3

# Colours for End Game Animation
.eqv	YELLOW_SPRINKLE	0xfffb85
.eqv 	PINK_HEART	0xff85d6
.eqv	GAME_OVER_CLR	0xf5dc1d
.eqv	BLUE_STAR	0x8bc5ff
.eqv	PURPLE_STAR	0xc6c3ff
.eqv	MOON_STAR	0xffd600
.eqv	RED_RAINBOW	0xff0b00
.eqv	YELLOW_RAINBOW	0xfff700
.eqv	GREEN_RAINBOW	0x53ff00
.eqv	BLUE_RAINBOW	0x3abaff
#####################################################################

.text
# Important register variables
#####################################################################
#	$t6 = fish location
#	$t7 = heart number status
#	$s0 = set address for draw_player_cat
#	$s1 = platform indicator
# 		how many pixels away from platform
# 	$s3 = key press address
#	$S4 = key press value
#	$s5 = 0th pixel of player character
#	$s6 = on fire indicator 
# 		(0 as not checked at all, 1 was recently on fire, so no need to check again)
# 	$s7 = winning indicator
#		how many pixels away from end cat
#####################################################################
.globl main
main:			j level_one

#####################################################################
############################# FILL BG ############################### 
#####################################################################
fill_bg:		li $t0, BASE_ADDRESS
			li $t1, BG_COLOUR
			# have to fill every 256 pixels in 64 rows 
			sw $t1, 0($t0)		# 0th pixel
			li $t2, 0		# pixel iterator
			li $t3, 16384		# maxholder

fill_bg_col:		beq $t2, $t3, exit_loop
			addi $t2, $t2, 4
			addi $t0, $t0, 4
			sw $t1, 0($t0)
			j fill_bg_col
		
exit_loop:		jr $ra				

#####################################################################
############################# LEVELS ################################ 
#####################################################################
level_one:		jal fill_bg
        		
        		li $t7, 3
        		li $s6, 0
        		li $s7, 100
        		
     			jal heart_status
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 2304
			addi $s0, $s0, 200
			jal draw_fem_cat
			la $a0, FEM_CAT_ADDRESS 	# get address
			la $a1, ($s0)			
   			sw $a1, 0($a0) 			# save new value
			
        		jal draw_sea_platform
        		
			jal draw_L1_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 2304
			addi $s0, $s0, 132
			la $t6, ($s0)
			jal draw_fish
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 11520
			addi $s0, $s0, 100
			jal draw_dead_fish
			la $a0, DEAD_FISH_ADDRESS
			la $a1, ($s0)
			sw $a1, 0($a0)
			
			# initial position of player cat should be on top of lowest platform
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 12288
			addi $s0, $s0, 32
			la $s5, ($s0)
			jal draw_player_cat
			
			# done drawing everything, now play the game through user input
			j user_input 

level_two:		jal draw_L1_animation
			jal draw_L1_trophy

			jal fill_bg
        		
        		li $t7, 3
        		li $s6, 0
        		li $s7, 100
        		
     			jal heart_status
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 2304
			addi $s0, $s0, 200
			jal draw_fem_cat
			la $a0, FEM_CAT_ADDRESS 	# get address
			la $a1, ($s0)			
   			sw $a1, 0($a0) 			# save new value
			
			jal draw_L2_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 6400
			addi $s0, $s0, 12
			la $t6, ($s0)
			jal draw_fish
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 5888
			addi $s0, $s0, 112
			jal draw_dead_fish
			la $a0, DEAD_FISH_ADDRESS
			la $a1, ($s0)
			sw $a1, 0($a0)
			
			# initial position of player cat should be on top of lowest platform
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 11776
			addi $s0, $s0, 116
			la $s5, ($s0)
			jal draw_player_cat
			
			# done drawing everything, now play the game through user input
			j user_input 
			
level_three:		jal draw_L2_animation
			jal draw_L2_trophy

			jal fill_bg
        		
        		li $t7, 3
        		li $s6, 0
        		li $s7, 100
        		
     			jal heart_status
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 2304
			addi $s0, $s0, 200
			jal draw_fem_cat
			la $a0, FEM_CAT_ADDRESS 	# get address
			la $a1, ($s0)			
   			sw $a1, 0($a0) 			# save new value
			
			jal draw_L3_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 13824
			addi $s0, $s0, 12
			la $t6, ($s0)
			jal draw_fish
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 2816
			addi $s0, $s0, 152
			jal draw_dead_fish
			la $a0, DEAD_FISH_ADDRESS
			la $a1, ($s0)
			sw $a1, 0($a0)
			
			# initial position of player cat should be on top of lowest platform
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 11776
			addi $s0, $s0, 224
			la $s5, ($s0)
			jal draw_player_cat
			
			# done drawing everything, now play the game through user input
			j user_input 
																																										
#####################################################################
########################### USER INPUT ############################## 
#####################################################################
key_presses:		# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			li $s4, KEY_ADDR 
			lw $s3, 0($s4) 
			
			# restore $ra from stack to go back to w_pressed function
			lw $ra, 0($sp)          
			addi $sp, $sp, 4
		
			jr $ra
			
user_input: 		# check for keyboard inputs 
			jal key_presses
			bne $s3, 1, no_key_pressed
			
			jal heart_status
			
			# if a key is pressed, then check the next word for what key
			lw $t2, 4($s4)
			beq $t2, KEY_W, w_pressed
			beq $t2, KEY_A, a_pressed
			beq $t2, KEY_S, s_pressed
			beq $t2, KEY_D, d_pressed
			beq $t2, KEY_P, p_pressed

check_gravity:		# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			# check how far from platform the player character is
			# if 1-3 pixels away, just wait
			la $t0, ($s5)
			addi $t0, $t0, 1024				# lowest character pixel line
			addi $t0, $t0, SHIFT_NEXT_ROW
			
			lw $t1, 0($t0)
			lw $t2, 24($t0)
			
			bne $t1, TOP_PLATFORM_CLR, apply_gravity	# left != PLATFORM, check right
			
			addi $sp, $sp, 4
			j user_input					# left == PLATFORM, don't move

apply_gravity:		beq $t2, TOP_PLATFORM_CLR, user_input		# left != PLATFORM == right, don't move
			
			# left != PLATFORM != right, start falling if no key pressed
			
			# wait for a couple ms
			li $v0, 32 
			li $a0, 400
			syscall
			
			# check for any key presses before applying gravity
			jal key_presses
			beq $s3, 1, user_input				# key presses => user_input
			
			# apply gravity
			jal check_game_over
			
			li $s2, 5
			j s_pressed
			
			# restore $ra from stack to go back to w_pressed function
			lw $ra, 0($sp)          
			addi $sp, $sp, 4
		
			jr $ra						# left != PLATFORM != right, allow movement	
			
no_key_pressed:		j check_gravity

p_pressed:		# restart game
			la $a0, LEVEL 		# get value
			li $a1, 1
   			sw $a1, 0($a0) 		# save new value
   			
			j main

w_pressed:		jal check_above_platform
			jal check_objects_collision
			jal check_win
			jal check_fish
			jal check_dead_fish
			
			# but if can only move 1 pixel above, perform one only
			la $t0, ($s5)
			addi $t0, $t0, -512
			lw $t1, 4($t0)				# pointer to 2nd level below lowest left character pixel
			lw $t2, 20($t0)				# pointer to 2nd level below lowest right character pixel
			beq $t1, BOTTOM_PLATFORM_CLR, w_pressed_one	# left == PLATFORM, 
			beq $t2, BOTTOM_PLATFORM_CLR, w_pressed_one	# right == PLATFORM
			
			# but if can only move 2 pixels above, perform two only
			la $t0, ($s5)
			addi $t0, $t0, -768
			lw $t1, 4($t0)				# pointer to 2nd level below lowest left character pixel
			lw $t2, 20($t0)				# pointer to 2nd level below lowest right character pixel
			beq $t1, BOTTOM_PLATFORM_CLR, w_pressed_two	# left == PLATFORM, 
			beq $t2, BOTTOM_PLATFORM_CLR, w_pressed_two	# right == PLATFORM
			
			# second check
			li $t1, 1
			li $t2, 2
			beq $s1, $t1, w_pressed_one
			beq $s1, $t2, w_pressed_two

			jal up_one_pixel
			jal animate_movement
			jal up_one_pixel
			jal animate_movement
			jal up_one_pixel
			jal animate_movement
			j user_input
			
w_pressed_one:		li $s1, 100
			jal up_one_pixel
			jal animate_movement
			j user_input

w_pressed_two:		li $s1, 100
			jal up_one_pixel
			jal animate_movement
			jal up_one_pixel
			jal animate_movement
			j user_input

a_pressed:		jal check_left_platform
			jal check_objects_collision
			jal check_win
			jal check_fish
			jal check_dead_fish

			# check how far away it is from platform by checking s1 value
			li $t1, 1
			li $t2, 2
			beq $s1, $t1, a_pressed_one
			beq $s1, $t2, a_pressed_two

			jal left_one_pixel
			jal animate_movement
			jal left_one_pixel
			jal animate_movement
			jal left_one_pixel
			jal animate_movement
			j user_input

a_pressed_one:		li $s1, 100
			jal left_one_pixel
			jal animate_movement
			j user_input

a_pressed_two:		li $s1, 100
			jal left_one_pixel
			jal animate_movement
			jal left_one_pixel
			jal animate_movement
			j user_input
									
s_pressed:		jal check_below_platform
			jal check_objects_collision
			jal check_win
			jal check_fish
			jal check_dead_fish

			# gravity
			beq $s2, 5, s_pressed_one
			
			# if can only move 1 pixel down, perform one only
			la $t0, ($s5)
			addi $t0, $t0, 1536
			lw $t1, 4($t0)				# pointer to 2nd level below lowest left character pixel
			lw $t2, 20($t0)				# pointer to 2nd level below lowest right character pixel
			beq $t1, TOP_PLATFORM_CLR, s_pressed_one	# left == PLATFORM, 
			beq $t2, TOP_PLATFORM_CLR, s_pressed_one	# right == PLATFORM
			
			# but if can only move 2 pixels down, perform two only
			la $t0, ($s5)
			addi $t0, $t0, 1792
			lw $t1, 4($t0)				# pointer to 2nd level below lowest left character pixel
			lw $t2, 20($t0)				# pointer to 2nd level below lowest right character pixel
			beq $t1, TOP_PLATFORM_CLR, s_pressed_two	# left == PLATFORM, 
			beq $t2, TOP_PLATFORM_CLR, s_pressed_two	# right == PLATFORM
			
			jal down_one_pixel
			jal animate_movement
			jal down_one_pixel
			jal animate_movement
			jal down_one_pixel
			jal animate_movement
			
			j user_input
			
s_pressed_one:		jal down_one_pixel
			jal animate_movement
			j user_input

s_pressed_two:		jal down_one_pixel
			jal animate_movement
			jal down_one_pixel
			jal animate_movement
			j user_input

d_pressed:		jal check_right_platform
			jal check_objects_collision
			jal check_win
			jal check_fish
			jal check_dead_fish
			
			# check how far away it is from platform by checking s1 value
			li $t1, 1
			li $t2, 2
			beq $s1, $t1, d_pressed_one
			beq $s1, $t2, d_pressed_two
			
			# check how far away it is from end cat by checking s7 value
			li $t1, 1
			li $t2, 2
			beq $s7, $t1, d_pressed_one
			beq $s7, $t2, d_pressed_two
			
			jal right_one_pixel
			jal animate_movement
			jal right_one_pixel
			jal animate_movement
			jal right_one_pixel
			j user_input

d_pressed_one:		li $s1, 100
			jal right_one_pixel
			jal animate_movement
			
			# for game end
			jal check_win
			j user_input

d_pressed_two:		li $s1, 100
			jal right_one_pixel
			jal animate_movement
			jal right_one_pixel
			jal animate_movement
			
			# for game end
			jal check_win
			j user_input
																																	
up_one_pixel:		# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			# if at the top max screen, don't do anything
			li $t1, BASE_ADDRESS
			addi $t1, $t1, 252
			blt $s5, $t1, user_input

        		# paint the pixels below the player black (5 pixels)
			la $t0, ($s5)
			li $t1, BG_COLOUR
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, 4($t0)
			sw $t1, 8($t0)
			sw $t1, 12($t0)
			sw $t1, 16($t0)
			sw $t1, 20($t0)
			
			# move player up by one pixel 
			la $t0, ($s5)
			addi $t0, $t0, -SHIFT_NEXT_ROW
			la $s0, ($t0)
			la $s5, ($t0)
			jal draw_player_cat
			
			jal draw_cur_platform
			
			# restore $ra from stack to go back to w_pressed function
			lw $ra, 0($sp)          
			addi $sp, $sp, 4
			
			jr $ra

left_one_pixel:		# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			# if at the left max screen, don't do anything
			li $t1, 256
			div $s5, $t1
			mfhi $t1
			beq $t1, $zero, user_input
			
        		# paint the right paw of player character black 
			la $t0, ($s5)
			li $t1, BG_COLOUR
			addi $t0, $t0, 24
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, ($t0)
			
			# move player to the left by one pixel 
			la $t0, ($s5)
			addi $t0, $t0, -4
			la $s0, ($t0)
			la $s5, ($t0)
			jal draw_player_cat
			
			jal draw_cur_platform
			
			# restore $ra from stack to go back to w_pressed function
			lw $ra, 0($sp)          
			addi $sp, $sp, 4
			
			jr $ra

down_one_pixel:		# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			# if at bottom max screen, don't do anything
			li $t1, BASE_ADDRESS
			addi $t1, $t1, 15103
			bgt $s5, $t1, user_input
			
        		# paint the pixels above the player black (5 pixels)
			la $t0, ($s5)
			li $t1, BG_COLOUR
			sw $t1, 4($t0)
			sw $t1, 20($t0)
			
			# move player down by one pixel 
			la $t0, ($s5)
			addi $t0, $t0, SHIFT_NEXT_ROW
			la $s0, ($t0)
			la $s5, ($t0)
			jal draw_player_cat
			
			jal draw_cur_platform
			
			# restore $ra from stack to go back to w_pressed function
			lw $ra, 0($sp)          
			addi $sp, $sp, 4
			
			jr $ra

right_one_pixel:	# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			# if at the right max screen, don't do anything
			li $t1, 256
			la $t2, ($s5)			# set t2 to point the rightmost pixel (first row)
			addi $t2, $t2, 24
			addi $t2, $t2, 4		# move it to first pixel of next line to check if its in the right max
			div $t2, $t1
			mfhi $t1
			beq $t1, $zero, user_input
			
        		# paint the left paw of player character black 
			la $t0, ($s5)
			li $t1, BG_COLOUR
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, ($t0)
			
			# move player to the right by one pixel 
			la $t0, ($s5)
			addi $t0, $t0, 4
			la $s0, ($t0)
			la $s5, ($t0)
			jal draw_player_cat
			
			jal draw_cur_platform
			
			# restore $ra from stack to go back to w_pressed function
			lw $ra, 0($sp)          
			addi $sp, $sp, 4
			
			jr $ra

animate_movement:	# apply sleep for smoother animation
			li $v0, 32 
			li $a0, 30
			syscall
			jr $ra

#####################################################################
######################## PLATFORM COLLISIONS ######################## 
#####################################################################

# detect platform collision
	# if below is platform colour (green), don't move down
	# if above is platform colour (brown), don't move up 
	# if right is platform colour (green/brown), don't move right
	# if left is platform colour (green/brown), don't move left

check_below_platform:	# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			la $t0, ($s5)
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			
			lw $t4, 0($t0)				# below lowest left black pixel
			lw $t1, 4($t0)				# below lowest left character pixel
			lw $t2, 20($t0)				# below lowest right character pixel
			lw $t3, 24($t0)				# below lowest right black pixel
			
			beq $t3, TOP_PLATFORM_CLR, user_input
			beq $t4, TOP_PLATFORM_CLR, user_input
			bne $t1, TOP_PLATFORM_CLR, check_below_right	# left != PLATFORM, check right
			
			addi $sp, $sp, 4
			j user_input					# left == PLATFORM, don't move

check_below_right:	beq $t2, TOP_PLATFORM_CLR, user_input		# left != PLATFORM == right, don't move

			# restore $ra from stack to go back to w_pressed function
			lw $ra, 0($sp)          
			addi $sp, $sp, 4
		
			jr $ra						# left != PLATFORM != right, allow movement

check_above_platform: 	# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			la $t0, ($s5)
			addi $t0, $t0, -SHIFT_NEXT_ROW
			lw $t1, 4($t0)				# left ear character pixel
			lw $t2, 20($t0)				# right ear character pixel
			
			# special case, when the platform is between the ears and hands, BLOCK movement
			la $t0, ($s5)
				# left first
			lw $t3, 0($t0)
			addi $t0, $t0, SHIFT_NEXT_ROW
			lw $t4, 0($t0)
			addi $t0, $t0, SHIFT_NEXT_ROW
			lw $t5, 0($t0)
			beq $t5, BOTTOM_PLATFORM_CLR, user_input
			beq $t4, BOTTOM_PLATFORM_CLR, s1_1
			beq $t3, BOTTOM_PLATFORM_CLR, s1_2
				# right 
			la $t0, ($s5)
			lw $t3, 24($t0)
			addi $t0, $t0, SHIFT_NEXT_ROW
			lw $t4, 24($t0)
			addi $t0, $t0, SHIFT_NEXT_ROW
			lw $t5, 24($t0)
			beq $t5, BOTTOM_PLATFORM_CLR, user_input
			beq $t4, BOTTOM_PLATFORM_CLR, s1_1
			beq $t3, BOTTOM_PLATFORM_CLR, s1_2
			
			bne $t1, BOTTOM_PLATFORM_CLR, check_above_right	# left != PLATFORM, check right

			addi $sp, $sp, 4
			j user_input					# left == PLATFORM, don't move

check_above_right:	beq $t2, BOTTOM_PLATFORM_CLR, user_input	# left != PLATFORM == right, don't move

			# restore $ra from stack to go back to w_pressed function
			lw $ra, 0($sp)          
			addi $sp, $sp, 4
			
			# check special condition
			lw $t3, 24($t0)
			beq $t3, BOTTOM_PLATFORM_CLR, user_input
		
			jr $ra						# left != PLATFORM != right, allow movement

check_right_platform: 	# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			# BLOCK move if beside is platform
			# using right HAND as indicator
			la $t0, ($s5)
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			lw $t1, 28($t0)				# besides the right hand character pixel
			
			beq $t1, TOP_PLATFORM_CLR, user_input
			beq $t1, BOTTOM_PLATFORM_CLR, user_input
			
			# using right EAR as indicator
			la $t0, ($s5)
			lw $t1, 28($t0)
			beq $t1, BOTTOM_PLATFORM_CLR, user_input
			
			# if not, check how far it is from platform and store it in $s1
			# using right HAND as indicator
			la $t0, ($s5)
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			li $s1, 100
			lw $t1, 32($t0)	
			beq $t1, TOP_PLATFORM_CLR, s1_1
			beq $t1, BOTTOM_PLATFORM_CLR, s1_1
			
			lw $t1, 36($t0)	
			beq $t1, TOP_PLATFORM_CLR, s1_2
			beq $t1, BOTTOM_PLATFORM_CLR, s1_2
			
			# using right EAR as indicator
			la $t0, ($s5)
			lw $t1, 28($t0)
			beq $t1, BOTTOM_PLATFORM_CLR, s1_1
			
			la $t0, ($s5)
			lw $t1, 32($t0)
			beq $t1, BOTTOM_PLATFORM_CLR, s1_2
			
			# special case
			addi $t0, $t0, 1024
			lw $t1, 28($t0)
			beq $t1, TOP_PLATFORM_CLR, user_input
			
			# restore $ra from stack to go back to d_pressed function
			lw $ra, 0($sp)          
			addi $sp, $sp, 4
			jr $ra

check_left_platform: 	# save $ra on stack from main function
			addi $sp, $sp, -4       
			sw $ra, 0($sp)
			
			# BLOCK move if beside is platform
			# using left HAND as indicator
			la $t0, ($s5)
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			addi $t0, $t0, SHIFT_NEXT_ROW
			lw $t1, -4($t0)				# besides the right hand character pixel
			
			beq $t1, TOP_PLATFORM_CLR, user_input
			beq $t1, BOTTOM_PLATFORM_CLR, user_input
			
			# using left EAR as indicator
			la $t0, ($s5)
			lw $t1, -4($t0)
