#####################################################################
######################## DRAWING FUNCTIONS ########################## 
#####################################################################
draw_cur_platform:	# access current platform by global variable LEVEL
			lw $t0, LEVEL
			beq $t0, 1, draw_L1_platform
			beq $t0, 2, draw_L2_platform
			beq $t0, 3, draw_L3_platform
						
draw_L1_platform:	# save $ra on stack from main function
			addi $sp, $sp, -4       
        		sw $ra, 0($sp)
        		
        		jal draw_top_platform
        		
        		li $s0, BASE_ADDRESS
			addi $s0, $s0, 13568
			addi $s0, $s0, 20	
			jal draw_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 12288
			addi $s0, $s0, 80	
			jal draw_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 9728
			addi $s0, $s0, 120	
			jal draw_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 7168
			addi $s0, $s0, 80	
			jal draw_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 5632
			addi $s0, $s0, 80
			jal draw_fire
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 5888
			addi $s0, $s0, 164	
			jal draw_platform
			
			lw $t0, FEM_CAT_ADDRESS
			la $s0, ($t0)
			jal draw_fem_cat
			
			# restore $ra from stack to go back to main function
			lw $ra, 0($sp)          
        		addi $sp, $sp, 4
			
			jr $ra

draw_L2_platform:	# save $ra on stack from main function
			addi $sp, $sp, -4       
        		sw $ra, 0($sp)
        		
        		jal draw_sea_platform
        		jal draw_top_right_platform
        		
        		li $s0, BASE_ADDRESS
			addi $s0, $s0, 13056
			addi $s0, $s0, 104	
			jal draw_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 9728
			addi $s0, $s0, 160	
			jal draw_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 7680
			addi $s0, $s0, 20	
			jal draw_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 8192
			addi $s0, $s0, 184
			jal draw_fire
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 6656
			addi $s0, $s0, 100	
			jal draw_platform
			
			lw $t0, FEM_CAT_ADDRESS
			la $s0, ($t0)
			jal draw_fem_cat
			
			# restore $ra from stack to go back to main function
			lw $ra, 0($sp)          
        		addi $sp, $sp, 4
			
			jr $ra

draw_L3_platform: 	# save $ra on stack from main function
			addi $sp, $sp, -4       
        		sw $ra, 0($sp)
        		
        		jal draw_sea_platform
