        		li $s0, BASE_ADDRESS
			addi $s0, $s0, 13056
			addi $s0, $s0, 204	
			jal draw_platform
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 12032
			addi $s0, $s0, 168	
