			li $s0, BASE_ADDRESS
			addi $s0, $s0, 8960
			addi $s0, $s0, 92	
			jal draw_platform
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 8960
			addi $s0, $s0, 116	
			jal draw_platform
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 10496
			addi $s0, $s0, 184
			jal draw_fire
			
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 5888
			addi $s0, $s0, 100	
			jal draw_platform
			li $s0, BASE_ADDRESS
			addi $s0, $s0, 4352
			addi $s0, $s0, 120
			jal draw_fire
			
			lw $t0, FEM_CAT_ADDRESS
			la $s0, ($t0)
			jal draw_fem_cat
			
