heart_status:		# save $ra on stack from main function
			addi $sp, $sp, -4       
        		sw $ra, 0($sp)
        		
   			beq $t7, 0, game_over
        		beq $t7, 1, draw_1_heart
        		beq $t7, 2, draw_2_hearts
        		beq $t7, 3, draw_3_hearts
        		
        		# bigger than 3:
        		li $t7, 3
        		
        		# restore $ra from stack to go back to main function
			lw $ra, 0($sp)          
        		addi $sp, $sp, 4
			jr $ra

draw_1_heart:		li $s0, BASE_ADDRESS
     			addi $s0, $s0, 512
        		jal draw_heart
        		
        		li $s0, BASE_ADDRESS
     			addi $s0, $s0, 512
     			addi $s0, $s0, 28
        		jal draw_black_heart
        		
        		li $s0, BASE_ADDRESS
     			addi $s0, $s0, 512
     			addi $s0, $s0, 28
     			addi $s0, $s0, 28
        		jal draw_black_heart
        		
        		# restore $ra from stack to go back to main function
			lw $ra, 0($sp)          
        		addi $sp, $sp, 4
			jr $ra

draw_2_hearts:		li $s0, BASE_ADDRESS
     			addi $s0, $s0, 512
        		jal draw_heart
        		
        		li $s0, BASE_ADDRESS
     			addi $s0, $s0, 512
     			addi $s0, $s0, 28
        		jal draw_heart
        		
        		li $s0, BASE_ADDRESS
     			addi $s0, $s0, 512
     			addi $s0, $s0, 28
     			addi $s0, $s0, 28
        		jal draw_black_heart
        		
        		# restore $ra from stack to go back to main function
			lw $ra, 0($sp)          
        		addi $sp, $sp, 4
			jr $ra

draw_3_hearts:		li $s0, BASE_ADDRESS
     			addi $s0, $s0, 512
        		jal draw_heart
        		
