     			addi $s0, $s0, 512
     			addi $s0, $s0, 28
        		jal draw_heart
        		
        		li $s0, BASE_ADDRESS
     			addi $s0, $s0, 512
     			addi $s0, $s0, 28
     			addi $s0, $s0, 28
        		jal draw_heart
        
        		# restore $ra from stack to go back to main function
			lw $ra, 0($sp)          
        		addi $sp, $sp, 4
			jr $ra

draw_black_heart: 	# 5 x 5 pixels needed
			la $t0, ($s0)
			addi $t0, $t0, 8
			li $t1, BLACK
			li $t2, BLACK
			
			sw $t1, 4($t0)
			sw $t1, 12($t0)
			
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, 0($t0)
			sw $t2, 4($t0)
			sw $t1, 8($t0)
			sw $t1, 12($t0)
			sw $t1, 16($t0)
			
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, 0($t0)
			sw $t1, 4($t0)
			sw $t1, 8($t0)
			sw $t1, 12($t0)
			sw $t1, 16($t0)
			
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, 4($t0)
			sw $t1, 8($t0)
			sw $t1, 12($t0)
			
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, 8($t0)
			
			jr $ra
																																																		
draw_heart: 		# 5 x 5 pixels needed
			la $t0, ($s0)
			addi $t0, $t0, 8
			li $t1, RED_HEART
			li $t2, WHITE
			
			sw $t1, 4($t0)
			sw $t1, 12($t0)
			
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, 0($t0)
			sw $t2, 4($t0)
			sw $t1, 8($t0)
			sw $t1, 12($t0)
			sw $t1, 16($t0)
			
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, 0($t0)
			sw $t1, 4($t0)
			sw $t1, 8($t0)
			sw $t1, 12($t0)
			sw $t1, 16($t0)
			
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, 4($t0)
			sw $t1, 8($t0)
			sw $t1, 12($t0)
			
			addi $t0, $t0, SHIFT_NEXT_ROW
			sw $t1, 8($t0)
			
			jr $ra

